<script>
import lang from '@/lang'
import { defineComponent, computed, ref } from '@vue/composition-api'
import {
  buildImageFromArrayBuffer,
  buildLinkHref
} from '@/utils/ADempiere/resource.js'
// import { uploadAttachment } from '@/api/ADempiere/user-interface/resources.js'
// import ImageCropper from '@theme/components/ImageCropper'
import request from '@/utils/request'
import axios from 'axios'
import store from '@/store'
import { showMessage } from '@/utils/ADempiere/notification'

export default defineComponent({
  name: 'Attachment',
  // components: { ImageCropper },
  props: {
    parentUuid: {
      type: String,
      required: false
    },
    containerUuid: {
      type: String,
      required: false
    },
    recordId: {
      type: Number,
      default: 0
    },
    tableName: {
      type: String,
      default: ''
    },
    recordUuid: {
      type: String,
      default: ''
    },
    isActiveTab: {
      type: Boolean,
      default: false
    }
  },
  setup(props, { root, refs }) {
    const dialogImageUrl = ref('')

    const dialogVisible = ref(false)

    const disabled = ref(false)
    const organizationBackground = ref('')
    const organizationImagePath = ref('')
    const currentImageOfProduct = ref('')
    const pdfAttachment = ref([])
    const newImage = ref([])
    const newListImage = ref([])
    const imageAttachment = ref([])
    const listImageAll = computed(() => {
      if (imageAttachment.value) {
        return imageAttachment.value.concat(pdfAttachment.value)
      }
      return []
    })
    const listImage = computed(() => {
      if (listImageAll) {
        return listImageAll.value.map(image => image.url)
      }
      return []
    })
    const Attachment = computed(() => {
      if (store.getters.getAttachment) {
        const cafe = store.getters.getAttachment.map(element => {
          if (element.content_type !== 'application/pdf') {
            return {
              ...element,
              image: converImage(element)
            }
          }
          return {
            ...element,
            image: converFile(element)
          }
        })
        return cafe
      }
      return store.getters.getAttachment
    })

    const fileList = ref([])

    const handleRemove = (file) => {
      console.log(file)
    }
    const handlePictureCardPreview = (file) => {
      dialogImageUrl.value = file.url
      dialogVisible.value = true
    }
    const handleDownload = async(file) => {
      const urlImage = await axios.get(file.qlq)
      const link = buildLinkHref({
        fileName: 'epae',
        outputStream: urlImage.data.result.data,
        type: file.type
      })
      link.click()
    }

    const converFile = (image) => {
      pdfAttachment.value.push({
        ...image,
        qlq: image.url.uri,
        type: image.content_type,
        uuid: image.resource_uuid,
        url: 'data:image/png;base64,'
      })
      return
    }
    const converImage = async(image) => {
      const urlImage = await axios.get(image.url.uri)
      imageAttachment.value.push({
        name: 'file',
        type: image.content_type,
        url: buildImageFromArrayBuffer({
          arrayBuffer: urlImage.data.result.data
        })
      })
      return urlImage.data.result.data
    }

    // const submitUpload = () => {
    //   const fmData = new FormData()
    //   fmData.append(
    //     listImageAll.value[0],
    //     data2blob(listImageAll.value[0].url, mime),
    //     listImageAll.value[0].type
    //   )
    //   console.log({
    //     tableName: props.tableName,
    //     recordId: props.recordId,
    //     recordUuid: props.recordUuid,
    //     list: listImageAll.value,
    //     fmData
    //   })
    //   uploadAttachment({
    //     tableName: props.tableName,
    //     recordId: props.recordId,
    //     recordUuid: props.recordUuid,
    //     list: listImageAll.value
    //   })
    // }
    const beforeAvatarUpload = (file) => {
      console.log({ file })
      listImageAll.value.push({
        name: file.name,
        type: file.type,
        status: 'newImage',
        uuid: file.uid,
        url: URL.createObjectURL(file)
      })
      newImage.value.push(URL.createObjectURL(file))
    }

    const upload = ref()

    const handleExceed = (files) => {
      upload.value.clearFiles()
      const file = files[0]
      upload.value.handleStart(file)
    }

    const submitUpload = () => {
      const form = document.getElementById('form')
      const formData = new FormData(form)
      request({
        url: 'http://0.0.0.:8085/api/adempiere/user-interface/component/attachment/save-attachment',
        method: 'post',
        data: formData
      })
        .then(resData => {
          showMessage({
            message: lang.t('window.containerInfo.attachment.success'),
            type: 'success'
          })
          refs.upload.submit()
          refs.upload.clearFiles()
          refs.upload.uploadFiles = []
        })
        .catch(err => {
          console.warn({ err })
          showMessage({
            message: lang.t('window.containerInfo.attachment.error'),
            type: 'error'
          })
        })
    }

    return {
      upload,
      dialogImageUrl,
      dialogVisible,
      disabled,
      newImage,
      newListImage,
      fileList,
      imageAttachment,
      pdfAttachment,
      organizationBackground,
      currentImageOfProduct,
      organizationImagePath,
      // computed
      listImage,
      listImageAll,
      Attachment,
      // methods
      // submitUpload,
      submitUpload,
      beforeAvatarUpload,
      converFile,
      // submitUploadEpal,
      handleExceed,
      // isEmptyValue,
      converImage,
      handleRemove,
      handlePictureCardPreview,
      handleDownload
    }
  }
})
</script>
